import _ from 'lodash';
export default {
  name: "Add",

  data() {
    return {
      activeIndex: '0',
      addForm: {
        goods_name: '',
        goods_price: '0',
        goods_weight: '0',
        goods_number: '0',
        // 商品所属的分类数组
        goods_cat: [],
        pics: [],
        goods_introduce: '',
        attrs: []
      },
      addFormRules: {
        goods_name: [{
          required: true,
          message: '请输入商品的名称',
          trigger: 'blur'
        }],
        goods_price: [{
          required: true,
          message: '请输入商品的价格',
          trigger: 'blur'
        }],
        goods_weight: [{
          required: true,
          message: '请输入商品的重量',
          trigger: 'blur'
        }],
        goods_number: [{
          required: true,
          message: '请输入商品的数量',
          trigger: 'blur'
        }],
        goods_cat: [{
          required: true,
          message: '请选择商品的分类',
          trigger: 'blur'
        }]
      },
      cateList: [],
      cateProps: {
        label: 'cat_name',
        value: 'cat_id',
        children: 'children',
        expandTrigger: 'hover'
      },
      manyTableData: [],
      onlyTableData: [],
      headersObj: {
        Authorization: window.sessionStorage.getItem('token')
      },
      previePath: '',
      previewVisible: false
    };
  },

  created() {
    this.getCateList();
  },

  methods: {
    async getCateList() {
      const {
        data: res
      } = await this.$http.get('categories');
      if (res.meta.status !== 200) return this.$message.error('获取商品分类列表失败！');
      this.cateList = res.data;
      console.log(this.cateList);
    },

    // 级联选择器选中项变化会触发
    handleChange() {
      if (this.addForm.goods_cat.length !== 3) {
        this.addForm.goods_cat = [];
      }
    },

    beforeTabLeave(activeName, oldActivename) {
      if (oldActivename === '0' && this.addForm.goods_cat.length !== 3) {
        this.$message.error('请先选择商品分类');
        return false;
      }
    },

    async tabClicked() {
      // 证明访问的是动态参数面板
      if (this.activeIndex === '1') {
        const {
          data: res
        } = await this.$http.get(`categories/${this.cateId}/attributes`, {
          params: {
            sel: 'many'
          }
        });
        if (res.meta.status !== 200) return this.$message.error('获取数据失败！');
        res.data.forEach(item => {
          item.attr_vals = item.attr_vals.length === 0 ? [] : item.attr_vals.split(' ');
        });
        this.manyTableData = res.data;
      }
    },

    handlePreview(file) {
      console.log(file);
      this.previePath = file.response.data.url;
      this.previewVisible = true;
    },

    handleRemove(file) {
      console.log(file);
      const filePath = file.response.data.tmp_path;
      const i = this.addForm.pics.findIndex(x => x.pic === filePath);
      this.addForm.pics.splice(i, 1);
      console.log(this.addForm);
    },

    handleSuccess(response) {
      const picInfo = response.data.tmp_path;
      this.addForm.pics.push(picInfo);
      console.log(this.addForm);
    },

    async add() {
      this.$refs.addFormRef.validate(async valid => {
        if (!valid) return this.$message.error('请填写必要的表单项目!');

        const form = _.cloneDeep(this.addForm);

        form.goods_cat = form.goods_cat.join(',');
        this.manyTableData.forEach(item => {
          const newInfo = {
            attr_id: item.attr_id,
            attr_value: item.attr_vals.join(' ')
          };
          this.addForm.attrs.push(newInfo);
        });
        this.onlyTableData.forEach(item => {
          const newInfo = {
            attr_id: item.attr_id,
            attr_value: item.attr_vals
          };
          this.addForm.attrs.push(newInfo);
        });
        form.attrs = this.addForm.attrs;
        const {
          data: res
        } = await this.$http.post('goods', form);
        if (res.meta.status !== 201) return this.$message.error('添加商品失败');
        this.$message.error('添加商品成功');
        this.$router.push('/goods');
      });
    }

  },
  computed: {
    cateId() {
      if (this.addForm.goods_cat === 3) {
        return this.addForm.goods_cat[2];
      }

      return null;
    }

  }
};
