import Vue from 'vue'
import VueRouter from 'vue-router'

// import Login from "@/components/Login";
const Login = () => import(/* webpackChunkName: "login_home_welcome"*/ '@/components/Login')
// import Home from "@/components/Home";
const Home = () => import(/* webpackChunkName: "login_home_welcome"*/ '@/components/Home')
// import Welcome from "@/components/Welcome";
const Welcome = () => import(/* webpackChunkName: "login_home_welcome"*/ '@/components/Welcome')
// import Users from "@/components/user/Users";
const Users = () => import(/* webpackChunkName: "users_rights_rules"*/ '@/components/user/Users')
// import Rights from "@/components/power/Rights";
const Rights = () => import(/* webpackChunkName: "users_rights_rules"*/ '@/components/power/Rights')
// import Roles from "@/components/power/Roles";
const Roles = () => import(/* webpackChunkName: "users_rights_rules"*/ '@/components/power/Roles')
// import List from "@/components/goods/List";
const List = () => import(/* webpackChunkName: "list_cate_params"*/ '@/components/goods/List')
// import Cate from "@/components/goods/Cate";
const Cate = () => import(/* webpackChunkName: "list_cate_params"*/ '@/components/goods/Cate')
// import Params from "@/components/goods/Params";
const Params = () => import(/* webpackChunkName: "list_cate_params"*/ '@/components/goods/Params')

import Add from "@/components/goods/Add";
import Order from "@/components/order/Order";
import Report from "@/components/report/Report";

Vue.use(VueRouter)

const routes = [
    {path: '/', redirect: '/login'},
    {path: '/login', component: Login},
    {
        path: '/home',
        component: Home,
        redirect: '/welcome',
        children: [
            {path: '/welcome', component: Welcome},
            {path: '/users', component: Users},
            {path: '/rights', component: Rights},
            {path: '/roles', component: Roles},
            {path: '/categories', component: Cate},
            {path: '/params', component: Params},
            {path: '/goods', component: List},
            {path: '/goods/add', component: Add},
            {path:'/orders', component: Order},
            {path:'/reports', component: Report},

        ]
    }
]

const router = new VueRouter({
    routes,
    mode: 'history'
})
// 挂载路由导航守卫
router.beforeEach ((to,from,next) => {
    if (to.path === '/login') return next();
    const tokenStr = window.sessionStorage.getItem('token')
    if (!tokenStr) return next('/login');
    next()

})

export default router
